const API_BASE_URL = 'http://localhost:3030';

class ApiService {
  async request(endpoint, options = {}) {
    const url = `${API_BASE_URL}${endpoint}`;
    const config = {
      headers: {
        'Content-Type': 'application/json',
        ...options.headers,
      },
      ...options,
    };

    console.log('游댯 Fazendo requisi칞칚o:', url);
    console.log('游댯 Config:', config);

    try {
      const response = await fetch(url, config);
      console.log('游릭 Response status:', response.status);
      console.log('游릭 Response ok:', response.ok);

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const text = await response.text();
      console.log('游릭 Response text:', text);

      const data = text ? JSON.parse(text) : {};
      console.log('游릭 Data parsed:', data);
      return data;
    } catch (error) {
      console.error('游댮 Erro na API:', error);
      return {
        status: false,
        status_code: 500,
        message: 'Erro de conex칚o com o servidor'
      };
    }
  }

  // Autentica칞칚o
  async login(chave) {
    return this.request('/v1/electrovision/login', {
      method: 'POST',
      body: JSON.stringify({ chave }),
    });
  }

  // Funcion치rios
  async getFuncionarios() {
    return this.request('/v1/electrovision/funcionarios');
  }

  async getFuncionario(id) {
    return this.request(`/v1/electrovision/funcionario/${id}`);
  }

  async createFuncionario(funcionario) {
    return this.request('/v1/electrovision/funcionario', {
      method: 'POST',
      body: JSON.stringify(funcionario),
    });
  }

  async updateFuncionario(id, funcionario) {
    return this.request(`/v1/electrovision/funcionario/${id}`, {
      method: 'PUT',
      body: JSON.stringify(funcionario),
    });
  }

  async deleteFuncionario(id) {
    return this.request(`/v1/electrovision/funcionario/${id}`, {
      method: 'DELETE',
    });
  }

  // Produtos
  async getProdutos() {
    return this.request('/v1/electrovision/produtos');
  }

  async getProduto(id) {
    return this.request(`/v1/electrovision/produto/${id}`);
  }

  async buscarProdutos(nome) {
    return this.request(`/v1/electrovision/produtos/buscar/${nome}`);
  }

  async createProduto(produto) {
    return this.request('/v1/electrovision/produto', {
      method: 'POST',
      body: JSON.stringify(produto),
    });
  }

  async updateProduto(id, produto) {
    return this.request(`/v1/electrovision/produto/${id}`, {
      method: 'PUT',
      body: JSON.stringify(produto),
    });
  }

  async deleteProduto(id) {
    return this.request(`/v1/electrovision/produto/${id}`, {
      method: 'DELETE',
    });
  }

  // Movimenta칞칫es
  async getMovimentacoes() {
    return this.request('/v1/electrovision/movimentacoes');
  }

  async getMovimentacoesProduto(id) {
    return this.request(`/v1/electrovision/movimentacoes/produto/${id}`);
  }

  async createMovimentacao(movimentacao) {
    return this.request('/v1/electrovision/movimentacao', {
      method: 'POST',
      body: JSON.stringify(movimentacao),
    });
  }
}

export default new ApiService();
