import React, { useState } from 'react';
import ApiService from '../services/api';

function Login({ onLogin }) {
  const [chave, setChave] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!chave.trim()) {
      setError('Por favor, insira sua chave de acesso');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const response = await ApiService.login(chave);
      if (response.status) {
        onLogin(response.dados);
      } else {
        setError(response.message || 'Erro ao fazer login');
      }
    } catch (error) {
      setError('Erro de conexão. Verifique se o servidor está rodando.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h2>ElectroVision</h2>
        <p style={{ textAlign: 'center', marginBottom: '2rem', color: '#7f8c8d' }}>
          Sistema de Controle de Estoque
        </p>

        {error && (
          <div className="alert alert-danger">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="chave">Chave de Acesso:</label>
            <input
              type="text"
              id="chave"
              value={chave}
              onChange={(e) => setChave(e.target.value)}
              placeholder="Digite sua chave de acesso"
              disabled={loading}
            />
          </div>

          <button 
            type="submit" 
            className="btn btn-primary" 
            style={{ width: '100%' }}
            disabled={loading}
          >
            {loading ? 'Entrando...' : 'Entrar'}
          </button>
        </form>
      </div>
    </div>
  );
}

export default Login;
