import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ApiService from '../services/api';

function Dashboard({ user, onLogout }) {
  const [movimentacoes, setMovimentacoes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadMovimentacoes();
  }, []);

  const loadMovimentacoes = async () => {
    try {
      const response = await ApiService.getMovimentacoes();
      if (response.status) {
        const lista = response.movimentacoes || response.dados || [];
        setMovimentacoes(lista.slice(0, 10));
      }
    } catch (error) {
      console.error('Erro ao carregar movimentações:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <header className="header">
        <h1>ElectroVision - Dashboard</h1>
        <div className="user-info">
          <span>Bem-vindo, {user.nome}</span>
          <button className="btn btn-secondary" onClick={onLogout}>
            Logout
          </button>
        </div>
      </header>

      <div className="container">
        <div className="dashboard-grid">
          <div className="dashboard-card">
            <h3>Cadastro de Produtos</h3>
            <p>Gerencie o catálogo de produtos eletrônicos</p>
            <Link to="/produtos" className="btn btn-primary">
              Acessar
            </Link>
          </div>

          <div className="dashboard-card">
            <h3>Gestão de Estoque</h3>
            <p>Controle movimentações de entrada e saída</p>
            <Link to="/estoque" className="btn btn-success">
              Acessar
            </Link>
          </div>
        </div>

        <div className="card" style={{ marginTop: '2rem' }}>
          <h3>Últimas Movimentações</h3>
          {loading ? (
            <p>Carregando...</p>
          ) : movimentacoes.length > 0 ? (
            <table className="table">
              <thead>
                <tr>
                  <th>Data</th>
                  <th>Resumo</th>
                  <th>Funcionário</th>
                </tr>
              </thead>
              <tbody>
                {movimentacoes.map((mov) => (
                  <tr key={mov.id_historico}>
                    <td>{new Date(mov.data).toLocaleDateString('pt-BR')}</td>
                    <td>
                      <span style={{
                        color: mov.resumo.startsWith('ENTRADA') ? '#27ae60' : '#e74c3c',
                        fontWeight: 'bold'
                      }}>
                        {mov.resumo}
                      </span>
                    </td>
                    <td>{mov.funcionario_nome}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p style={{ textAlign: 'center', padding: '2rem', color: '#7f8c8d' }}>
              Nenhuma movimentação registrada
            </p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
