import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ApiService from '../services/api';

function CadastroProduto({ user, onLogout }) {
  const [produtos, setProdutos] = useState([]);
  const [filteredProdutos, setFilteredProdutos] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);
  const [formData, setFormData] = useState({
    nome: '',
    especificacao: '',
    qtd: '',
    prateleira: ''
  });

  useEffect(() => {
    loadProdutos();
  }, []);

  useEffect(() => {
    if (searchTerm) {
      const filtered = produtos.filter(produto =>
        produto.nome.toLowerCase().includes(searchTerm.toLowerCase()) ||
        produto.especificacao.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredProdutos(filtered);
    } else {
      setFilteredProdutos(produtos);
    }
  }, [searchTerm, produtos]);

  const loadProdutos = async () => {
    setLoading(true);
    try {
      const response = await ApiService.getProdutos();
      console.log('üì¶ Response produtos:', response);
      if (response.status && response.produtos) {
        setProdutos(response.produtos);
      } else if (response.status && response.dados) {
        setProdutos(response.dados);
      } else {
        setError(response.message || 'Erro ao carregar produtos');
      }
    } catch (error) {
      setError('Erro de conex√£o');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async () => {
    if (!searchTerm.trim()) {
      setFilteredProdutos(produtos);
      return;
    }

    try {
      const response = await ApiService.buscarProdutos(searchTerm);
      if (response.status && response.produtos) {
        setFilteredProdutos(response.produtos);
      } else if (response.status && response.dados) {
        setFilteredProdutos(response.dados);
      }
    } catch (error) {
      console.error('Erro na busca:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.nome || !formData.especificacao || !formData.qtd || !formData.prateleira) {
      setError('Todos os campos s√£o obrigat√≥rios');
      return;
    }

    if (isNaN(formData.qtd) || parseInt(formData.qtd) < 0) {
      setError('Quantidade deve ser um n√∫mero v√°lido');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const productData = {
        ...formData,
        qtd: parseInt(formData.qtd)
      };

      let response;
      if (editingProduct) {
        response = await ApiService.updateProduto(editingProduct.id, productData);
        console.log('‚úèÔ∏è Response update:', response);
      } else {
        response = await ApiService.createProduto(productData);
        console.log('‚ûï Response create:', response);
      }

      if (response.status) {
        setSuccess(editingProduct ? 'Produto atualizado com sucesso!' : 'Produto cadastrado com sucesso!');
        setShowModal(false);
        setEditingProduct(null);
        setFormData({ nome: '', especificacao: '', qtd: '', prateleira: '' });
        setTimeout(() => setSuccess(''), 3000);
        loadProdutos();
      } else {
        setError(response.message || 'Erro ao salvar produto');
      }
    } catch (error) {
      setError('Erro de conex√£o');
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (produto) => {
    setEditingProduct(produto);
    setFormData({
      nome: produto.nome,
      especificacao: produto.especificacao,
      qtd: produto.qtd.toString(),
      prateleira: produto.prateleira
    });
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Tem certeza que deseja excluir este produto?')) {
      return;
    }

    setLoading(true);
    setError('');
    setSuccess('');
    try {
      const response = await ApiService.deleteProduto(id);
      console.log('üóëÔ∏è Response delete:', response);
      if (response.status) {
        setSuccess('Produto exclu√≠do com sucesso!');
        setTimeout(() => setSuccess(''), 3000);
        loadProdutos();
      } else {
        setError(response.message || 'Erro ao excluir produto');
      }
    } catch (error) {
      setError('Erro de conex√£o');
    } finally {
      setLoading(false);
    }
  };

  const handleQuickStock = async (produto, tipo) => {
    setError('');
    setSuccess('');

    if (tipo === 'saida' && produto.qtd === 0) {
      setError('Estoque insuficiente');
      return;
    }

    try {
      const novaQtd = tipo === 'entrada' ? produto.qtd + 1 : produto.qtd - 1;

      const produtoAtualizado = {
        nome: produto.nome,
        especificacao: produto.especificacao,
        qtd: novaQtd,
        prateleira: produto.prateleira
      };

      console.log('üì¶ Atualizando produto:', produtoAtualizado);
      const response = await ApiService.updateProduto(produto.id, produtoAtualizado);
      console.log('üì¶ Response quick stock:', response);

      if (response.status) {
        setSuccess(`${tipo === 'entrada' ? '+1' : '-1'} ${produto.nome}`);
        setTimeout(() => setSuccess(''), 2000);
        loadProdutos();
      } else {
        setError(response.message || 'Erro ao atualizar estoque');
        setTimeout(() => setError(''), 3000);
      }
    } catch (error) {
      setError('Erro de conex√£o');
    }
  };

  const openModal = () => {
    setEditingProduct(null);
    setFormData({ nome: '', especificacao: '', qtd: '', prateleira: '' });
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setEditingProduct(null);
    setError('');
  };

  return (
    <div>
      <header className="header">
        <h1>Cadastro de Produtos</h1>
        <div className="user-info">
          <span>Usu√°rio: {user.nome}</span>
          <Link to="/dashboard" className="btn btn-secondary">Voltar</Link>
          <button className="btn btn-secondary" onClick={onLogout}>Logout</button>
        </div>
      </header>

      <div className="container">
        {error && <div className="alert alert-danger">{error}</div>}
        {success && <div className="alert alert-success">{success}</div>}

        <div className="card">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
            <h3>Produtos Cadastrados</h3>
            <button className="btn btn-primary" onClick={openModal}>
              Novo Produto
            </button>
          </div>

          <div className="search-box">
            <input
              type="text"
              placeholder="Buscar produtos..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
            />
          </div>

          {loading ? (
            <p>Carregando...</p>
          ) : (
            <table className="table">
              <thead>
                <tr>
                  <th>Nome</th>
                  <th>Especifica√ß√£o</th>
                  <th>Quantidade</th>
                  <th>Prateleira</th>
                  <th>A√ß√µes</th>
                </tr>
              </thead>
              <tbody>
                {filteredProdutos.map((produto) => (
                  <tr key={produto.id}>
                    <td>{produto.nome}</td>
                    <td>{produto.especificacao}</td>
                    <td>
                      <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                        <button 
                          className="btn btn-danger" 
                          onClick={() => handleQuickStock(produto, 'saida')}
                          style={{ padding: '0.25rem 0.5rem', fontSize: '0.8rem' }}
                          disabled={produto.qtd === 0}
                        >
                          -
                        </button>
                        <span style={{ minWidth: '30px', textAlign: 'center', fontWeight: 'bold' }}>
                          {produto.qtd}
                        </span>
                        <button 
                          className="btn btn-success" 
                          onClick={() => handleQuickStock(produto, 'entrada')}
                          style={{ padding: '0.25rem 0.5rem', fontSize: '0.8rem' }}
                        >
                          +
                        </button>
                      </div>
                    </td>
                    <td>{produto.prateleira}</td>
                    <td>
                      <div className="actions">
                        <button 
                          className="btn btn-warning" 
                          onClick={() => handleEdit(produto)}
                        >
                          Editar
                        </button>
                        <button 
                          className="btn btn-danger" 
                          onClick={() => handleDelete(produto.id)}
                        >
                          Excluir
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}

          {filteredProdutos.length === 0 && !loading && (
            <p style={{ textAlign: 'center', padding: '2rem', color: '#7f8c8d' }}>
              Nenhum produto encontrado
            </p>
          )}
        </div>
      </div>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <div className="modal-header">
              <h3>{editingProduct ? 'Editar Produto' : 'Novo Produto'}</h3>
              <button className="close-btn" onClick={closeModal}>&times;</button>
            </div>

            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Nome do Produto:</label>
                <input
                  type="text"
                  value={formData.nome}
                  onChange={(e) => setFormData({...formData, nome: e.target.value})}
                  placeholder="Ex: iPhone 14 Pro"
                />
              </div>

              <div className="form-group">
                <label>Especifica√ß√£o:</label>
                <textarea
                  value={formData.especificacao}
                  onChange={(e) => setFormData({...formData, especificacao: e.target.value})}
                  placeholder="Ex: 128GB, Tela 6.1', C√¢mera 48MP, iOS 16"
                  rows="3"
                />
              </div>

              <div className="form-group">
                <label>Quantidade:</label>
                <input
                  type="number"
                  value={formData.qtd}
                  onChange={(e) => setFormData({...formData, qtd: e.target.value})}
                  min="0"
                />
              </div>

              <div className="form-group">
                <label>Prateleira:</label>
                <input
                  type="text"
                  value={formData.prateleira}
                  onChange={(e) => setFormData({...formData, prateleira: e.target.value})}
                  placeholder="Ex: A1, B2, C3"
                />
              </div>

              <div className="actions">
                <button type="submit" className="btn btn-success" disabled={loading}>
                  {loading ? 'Salvando...' : 'Salvar'}
                </button>
                <button type="button" className="btn btn-secondary" onClick={closeModal}>
                  Cancelar
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default CadastroProduto;
