import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ApiService from '../services/api';

function GestaoEstoque({ user, onLogout }) {
  const [produtos, setProdutos] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState('');
  const [tipoMovimentacao, setTipoMovimentacao] = useState('entrada');
  const [quantidade, setQuantidade] = useState('');
  const [data, setData] = useState(new Date().toISOString().split('T')[0]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [alert, setAlert] = useState(null);

  useEffect(() => {
    loadProdutos();
  }, []);

  const loadProdutos = async () => {
    setLoading(true);
    try {
      const response = await ApiService.getProdutos();
      console.log('üì¶ Response produtos (estoque):', response);
      if (response.status) {
        const listaProdutos = response.produtos || response.dados || [];
        // Ordena√ß√£o alfab√©tica (Bubble Sort)
        const produtosOrdenados = [...listaProdutos];
        for (let i = 0; i < produtosOrdenados.length - 1; i++) {
          for (let j = 0; j < produtosOrdenados.length - i - 1; j++) {
            if (produtosOrdenados[j].nome > produtosOrdenados[j + 1].nome) {
              [produtosOrdenados[j], produtosOrdenados[j + 1]] = [produtosOrdenados[j + 1], produtosOrdenados[j]];
            }
          }
        }
        setProdutos(produtosOrdenados);
      } else {
        setError(response.message || 'Erro ao carregar produtos');
      }
    } catch (error) {
      setError('Erro de conex√£o');
    } finally {
      setLoading(false);
    }
  };

  const handleMovimentacao = async (e) => {
    e.preventDefault();

    if (!selectedProduct || !quantidade || !data) {
      setError('Todos os campos s√£o obrigat√≥rios');
      return;
    }

    if (isNaN(quantidade) || parseInt(quantidade) <= 0) {
      setError('Quantidade deve ser um n√∫mero positivo');
      return;
    }

    setLoading(true);
    setError('');
    setSuccess('');
    setAlert(null);

    try {
      const movimentacao = {
        id_produto: parseInt(selectedProduct),
        id_funcionario: user.id,
        tipo: tipoMovimentacao,
        quantidade: parseInt(quantidade),
        data: data
      };

      const response = await ApiService.createMovimentacao(movimentacao);

      if (response.status) {
        setSuccess(`Movimenta√ß√£o de ${tipoMovimentacao} realizada com sucesso!`);

        // Verificar se h√° alerta de estoque
        if (response.alerta_estoque) {
          setAlert(response.alerta_estoque);
        }

        // Limpar formul√°rio
        setSelectedProduct('');
        setQuantidade('');
        setTipoMovimentacao('entrada');

        // Recarregar produtos para atualizar quantidades
        loadProdutos();
      } else {
        setError(response.message || 'Erro ao realizar movimenta√ß√£o');
      }
    } catch (error) {
      setError('Erro de conex√£o');
    } finally {
      setLoading(false);
    }
  };

  const getEstoqueStatus = (quantidade) => {
    if (quantidade === 0) {
      return { status: 'ESGOTADO', color: '#e74c3c' };
    } else if (quantidade < 5) {
      return { status: 'ESTOQUE BAIXO', color: '#f39c12' };
    } else {
      return { status: 'NORMAL', color: '#27ae60' };
    }
  };

  return (
    <div>
      <header className="header">
        <h1>Gest√£o de Estoque</h1>
        <div className="user-info">
          <span>Usu√°rio: {user.nome}</span>
          <Link to="/dashboard" className="btn btn-secondary">Voltar</Link>
          <button className="btn btn-secondary" onClick={onLogout}>Logout</button>
        </div>
      </header>

      <div className="container">
        {error && <div className="alert alert-danger">{error}</div>}
        {success && <div className="alert alert-success">{success}</div>}
        {alert && (
          <div className="alert alert-warning">
            <strong>{alert.message}</strong><br />
            Produto: {alert.produto}<br />
            Quantidade atual: {alert.quantidade_atual}<br />
            Status: {alert.status}
          </div>
        )}

        <div className="card">
          <h3>Nova Movimenta√ß√£o</h3>
          <form onSubmit={handleMovimentacao}>
            <div className="form-group">
              <label>Produto:</label>
              <select
                value={selectedProduct}
                onChange={(e) => setSelectedProduct(e.target.value)}
                disabled={loading}
              >
                <option value="">Selecione um produto</option>
                {produtos.map((produto) => (
                  <option key={produto.id} value={produto.id}>
                    {produto.nome} - Qtd: {produto.qtd} - {produto.prateleira}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Tipo de Movimenta√ß√£o:</label>
              <select
                value={tipoMovimentacao}
                onChange={(e) => setTipoMovimentacao(e.target.value)}
                disabled={loading}
              >
                <option value="entrada">Entrada</option>
                <option value="saida">Sa√≠da</option>
              </select>
            </div>

            <div className="form-group">
              <label>Quantidade:</label>
              <input
                type="number"
                value={quantidade}
                onChange={(e) => setQuantidade(e.target.value)}
                min="1"
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>Data:</label>
              <input
                type="date"
                value={data}
                onChange={(e) => setData(e.target.value)}
                disabled={loading}
              />
            </div>

            <button type="submit" className="btn btn-primary" disabled={loading}>
              {loading ? 'Processando...' : 'Registrar Movimenta√ß√£o'}
            </button>
          </form>
        </div>

        <div className="card">
          <h3>Produtos em Estoque (Ordem Alfab√©tica)</h3>
          {loading ? (
            <p>Carregando...</p>
          ) : (
            <table className="table">
              <thead>
                <tr>
                  <th>Nome</th>
                  <th>Especifica√ß√£o</th>
                  <th>Quantidade</th>
                  <th>Prateleira</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {produtos.map((produto) => {
                  const status = getEstoqueStatus(produto.qtd);
                  return (
                    <tr key={produto.id}>
                      <td>{produto.nome}</td>
                      <td>{produto.especificacao}</td>
                      <td>{produto.qtd}</td>
                      <td>{produto.prateleira}</td>
                      <td>
                        <span style={{ 
                          color: status.color, 
                          fontWeight: 'bold',
                          padding: '0.25rem 0.5rem',
                          borderRadius: '4px',
                          backgroundColor: status.color + '20'
                        }}>
                          {status.status}
                        </span>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          )}

          {produtos.length === 0 && !loading && (
            <p style={{ textAlign: 'center', padding: '2rem', color: '#7f8c8d' }}>
              Nenhum produto cadastrado
            </p>
          )}
        </div>

        <div className="card">
          <h3>Legenda de Status</h3>
          <div style={{ display: 'flex', gap: '2rem', flexWrap: 'wrap' }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
              <div style={{ 
                width: '20px', 
                height: '20px', 
                backgroundColor: '#27ae60', 
                borderRadius: '4px' 
              }}></div>
              <span>Normal (‚â• 5 unidades)</span>
            </div>
            <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
              <div style={{ 
                width: '20px', 
                height: '20px', 
                backgroundColor: '#f39c12', 
                borderRadius: '4px' 
              }}></div>
              <span>Estoque Baixo (1-4 unidades)</span>
            </div>
            <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
              <div style={{ 
                width: '20px', 
                height: '20px', 
                backgroundColor: '#e74c3c', 
                borderRadius: '4px' 
              }}></div>
              <span>Esgotado (0 unidades)</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default GestaoEstoque;
